		while (true) {
			w = scanner.nextInt();
			h = scanner.nextInt();
			if ((w | h) == 0)
				break;
			map = new char[h][w];
			v = new boolean[h][w];
			for (int i = 0; i < h; i++) {
				map[i] = scanner.next().toCharArray();
				for (int j = 0; j < w; j++) {
					if (map[i][j] != '.') {
						v[i][j] = true;
					}
				}
			}
			ans = new int[4];
			for (int i = 0; i < h; i++)
				for (int j = 0; j < w; j++) {

					if (v[i][j])
						continue;
					u = 0;
					int ret  = dfs(i, j);
					ans[u] += ret;
				}
			System.out.println(ans[1] + " " + ans[2]);
		}
	}

	private int dfs(int y, int x) {
		int ret = 0;
		if (v[y][x])
			return ret;
		v[y][x] = true;
		ret++;
		for (int[] m : move) {
			int ny = y + m[0];
			int nx = x + m[1];
			if (!isOK(ny, nx))
				continue;
			if (map[ny][nx] == 'B') {
				u |= 1;
				continue;
			} else if (map[ny][nx] == 'W') {
				u |= 2;
				continue;
			}
			ret += dfs(ny, nx);
		}
		return ret;
	}

	private boolean isOK(int y, int x) {
		return 0 <= y && y < h && 0 <= x && x < w;
	}

	int w, h, u, ans[];
	boolean[][] v;
	char[][] map;
	int[][] move = { { -1, 0 }, { 0, -1 }, { 0, 1 }, { 1, 0 } };
}

import java.util.Scanner;
public class Main
{
	static int field[][];
	static int white=0,black=0,cnt,fb,fw;
	static int W,H;
	public static void main(String[] args) 
	{
		Scanner in=new Scanner(System.in);
		for(;;)
		{
			W=in.nextInt();
			H=in.nextInt();
			if((W|H)==0)
				return;
			field=new int[H][W];
			for(int i=0;i<H;i++)
			{
				String st=in.next();
				for(int j=0;j<W;j++)
				{
					if(st.charAt(j)=='.')
						field[i][j]=0;
					else if(st.charAt(j)=='W')
						field[i][j]=1;
					else if(st.charAt(j)=='B')
						field[i][j]=2;
				}
			}
			black=0;
			white=0;
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					cnt=0;
					fb=0;
					fw=0;
					if(field[i][j]==0)
						solve(i,j);
				}
			}
			System.out.println(black+" "+white);
		}	
	}
	public static void solve(int h,int w)
	{
		field[h][w]=3;
		cnt++;
		if(h-1>=0&&field[h-1][w]==1)
			fw=1;
		else if(h-1>=0&&field[h-1][w]==2)
			fb=1;
		else if(h+1<H&&field[h+1][w]==1)
			fw=1;
		else if(h+1<H&&field[h+1][w]==2)
			fb=1;
		else if(w-1>=0&&field[h][w-1]==1)
			fw=1;
		else if(w-1>=0&&field[h][w-1]==2)
			fb=1;
		else if(w+1<W&&field[h][w+1]==1)
			fw=1;
		else if(w+1<W&&field[h][w+1]==2)
			fb=1;

		if(h-1>=0&&field[h-1][w]==0)
			solve(h-1,w);
		if(h+1<H&&field[h+1][w]==0)
			solve(h+1,w);
		if(w-1>=0&&field[h][w-1]==0)
			solve(h,w-1);
		if(w+1<W&&field[h][w+1]==0)
			solve(h,w+1);

		if(fw==1&&fb==0)
		{
			white+=cnt;
			cnt=0;
			return;
		}
		else if(fw==0&&fb==1)
		{
			black+=cnt;
			cnt=0;
			return;
		}
		else
		{
			cnt=0;
			return;
		}

	}
}

import java.util.*;

class Main{
    int h,w;
    char[][] grid;
    Boolean B,W;
    int countB,countW;
    int[] dx = {1,-1,0,0};
    int[] dy = {0,0,1,-1};

    void solve(){
	Scanner sc = new Scanner(System.in);
	w = sc.nextInt();
	while(w!=0){
	    h = sc.nextInt();
	    grid = new char[h][w];
	    for(int i=0; i<h; i++){
		String line = sc.next();
		grid[i] = line.toCharArray();
	    }

	    countB = 0;
	    countW = 0;
	    for(int i=0; i<h; i++){
		for(int j=0; j<w; j++){
		    if(grid[i][j]=='.'){
			B = false;
			W = false;
			dfs(j,i);
			if(B&&!W)countB++;
			if(!B&&W)countW++;
		    }
		}
	    }

	    System.out.println(countB+" "+countW);

	    w = sc.nextInt();
	}
    }

    void dfs(int x, int y){
	if(B && W)return;
	for(int i=0; i<4; i++){
	    int nx = x+dx[i];
	    int ny = y+dy[i];
	    if(nx<w && nx>=0 && ny<h && ny>=0){
		if(grid[ny][nx]=='B')B = true;
		else if(grid[ny][nx]=='W')W = true;
		else if(grid[ny][nx]=='.'){
		    grid[y][x] = 'x';
		    dfs(nx,ny);
		    grid[y][x] = '.';
		}
	    }
	}
    }

    public static void main(String[] args){
	new Main().solve();
    }
}

