public class Main {

	int w, h;
	int[][] W, B;

	void run() {
		Scanner sc = new Scanner(System.in);

		for(;;) {
			w = sc.nextInt();
			h = sc.nextInt();
			if( (w|h) == 0 ) break;

			W = new int[h+2][w+2];
			B = new int[h+2][w+2];

			for(int[] a: W) fill(a, 1);
			for(int[] a: B) fill(a, -1);

			boolean flg = false;
			for(int i=1;i<=h;i++) {
				String str = sc.next();
				for(int j=1;j<=w;j++) {
					if( str.charAt(j-1) == 'W' ) { flg = true; W[i][j] = B[i][j] = 1; }
					else if( str.charAt(j-1) == 'B' ) { flg = true; W[i][j] = B[i][j] = -1;}
					else W[i][j] = B[i][j] = 0;
				}
			}

			int wc = 0, bc = 0;

			if(flg)
			for(int i=1;i<=h;i++) for(int j=1;j<=w;j++) {
				int c = 0;
				if( W[i][j] == 0 ) c = dfs(j,i,1, W);
				if(c != 0) debug( j, i, c, "W" );
				wc += max(c, 0);
				c  = 0;
				if( B[i][j] == 0 ) c = dfs(j,i,-1, B);
				if(c != 0) debug(j, i, c, "B");
				bc += max(c, 0);
			}

///			for(int[] a: W) debug(a);
//			for(int[] a: B) debug(a);

			System.out.println(bc + " " + wc);
		}
	}

	int[] dx = {-1,0,1,0};
	int[] dy = {0,-1,0,1};

	int dfs(int x, int y, int c, int[][] map) {
		if( map[y][x] == -c ) return -1;
		map[y][x] = c;

		int cnt = 1;
		for(int i=0;i<4;i++) if( map[y+dy[i]][x+dx[i]] != c ) {
			int cc = dfs(x+dx[i], y+dy[i], c, map);
			if( cc == -1 ) {cnt = -1; }
			if( cnt != -1 ) cnt += cc;
		}

		return cnt;
	}

	public static void main(String[] args) {
		new Main().run();
	}

	void debug( Object... o ) {
//		System.err.println(deepToString(o));
	}

}

import java.io.IOException;
import java.util.Scanner;

public class Main {

	public static void main(String[] args) throws IOException {
		Scanner sc = new Scanner(System.in);

		while (true) {
			final int w = sc.nextInt();
			final int h = sc.nextInt();

			if (w == 0 && h == 0) {
				break;
			}

			int[][] map = new int[h][w];

			for (int i = 0; i < h; i++) {
				char[] input = sc.next().toCharArray();

				for (int j = 0; j < w; j++) {
					if (input[j] == 'W') {
						map[i][j] = 1;
					} else if (input[j] == 'B') {
						map[i][j] = -1;
					} else {
						map[i][j] = 0;
					}
				}
			}

			int[][] w_dist = new int[h][w];
			int[][] b_dist = new int[h][w];

			for (int i = 0; i < h; i++) {
				for (int j = 0; j < w; j++) {
					w_dist[i][j] = Integer.MAX_VALUE;
					b_dist[i][j] = Integer.MAX_VALUE;
				}
			}

			while(true){
				boolean updated = false;

				// w-forward
				for (int i = 0; i < h; i++) {
					for (int j = 0; j < w; j++) {
						if (map[i][j] == -1) {
							w_dist[i][j] = Integer.MAX_VALUE;
							continue;
						} else if (map[i][j] == 1) {
							w_dist[i][j] = 0;
							continue;
						}

						int cur = w_dist[i][j];

						if (i == 0 && j == 0) {
							w_dist[i][j] = Math.min(Integer.MAX_VALUE, w_dist[i][j]);
						} else if (i == 0) {
							if (w_dist[i][j - 1] != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(w_dist[i][j - 1] + 1, w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(w_dist[i][j - 1], w_dist[i][j]);
							}
						} else if (j == 0) {
							if (w_dist[i - 1][j] != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(w_dist[i - 1][j] + 1, w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(w_dist[i - 1][j], w_dist[i][j]);
							}
						} else {
							final int min = Math.min(w_dist[i - 1][j],
									w_dist[i][j - 1]);
							if (min != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(min + 1, w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(min, w_dist[i][j]);
							}
						}

						if(w_dist[i][j] != cur){
							updated = true;
						}
					}
				}
				// w-back
				for (int i = h - 1; i >= 0; i--) {
					for (int j = w - 1; j >= 0; j--) {
						if (map[i][j] == -1) {
							w_dist[i][j] = Integer.MAX_VALUE;
							continue;
						} else if (map[i][j] == 1) {
							w_dist[i][j] = 0;
							continue;
						}

						int cur = w_dist[i][j];

						if (i == h - 1 && j == w - 1) {
							w_dist[i][j] = Math.min(Integer.MAX_VALUE,
									w_dist[i][j]);
						} else if (i == h - 1) {
							if (w_dist[i][j + 1] != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(w_dist[i][j + 1] + 1,
										w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(w_dist[i][j + 1],
										w_dist[i][j]);
							}
						} else if (j == w - 1) {
							if (w_dist[i + 1][j] != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(w_dist[i + 1][j] + 1,
										w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(w_dist[i + 1][j],
										w_dist[i][j]);
							}
						} else {
							final int min = Math.min(w_dist[i + 1][j],
									w_dist[i][j + 1]);
							if (min != Integer.MAX_VALUE) {
								w_dist[i][j] = Math.min(min + 1, w_dist[i][j]);
							} else {
								w_dist[i][j] = Math.min(min, w_dist[i][j]);
							}
						}

						if(w_dist[i][j] != cur){
							updated = true;
						}
					}
				}

				// b-forward
				for (int i = 0; i < h; i++) {
					for (int j = 0; j < w; j++) {
						if (map[i][j] == 1) {
							b_dist[i][j] = Integer.MAX_VALUE;
							continue;
						} else if (map[i][j] == -1) {
							b_dist[i][j] = 0;
							continue;
						}

						int cur = b_dist[i][j];

						if (i == 0 && j == 0) {
							b_dist[i][j] = Integer.MAX_VALUE;
						} else if (i == 0) {
							if (b_dist[i][j - 1] != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(b_dist[i][j - 1] + 1, b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(b_dist[i][j - 1], b_dist[i][j]);
							}
						} else if (j == 0) {
							if (b_dist[i - 1][j] != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(b_dist[i - 1][j] + 1, b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(b_dist[i - 1][j], b_dist[i][j]);
							}
						} else {
							final int min = Math.min(b_dist[i - 1][j],
									b_dist[i][j - 1]);
							if (min != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(min + 1, b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(min, b_dist[i][j]);
							}
						}

						if(b_dist[i][j] != cur){
							updated = true;
						}
					}
				}

				// b-back
				for (int i = h - 1; i >= 0; i--) {
					for (int j = w - 1; j >= 0; j--) {
						if (map[i][j] == 1) {
							b_dist[i][j] = Integer.MAX_VALUE;
							continue;
						} else if (map[i][j] == -1) {
							b_dist[i][j] = 0;
							continue;
						}

						int cur = b_dist[i][j];

						if (i == h - 1 && j == w - 1) {
							b_dist[i][j] = Math.min(Integer.MAX_VALUE,
									b_dist[i][j]);
						} else if (i == h - 1) {
							if (b_dist[i][j + 1] != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(b_dist[i][j + 1] + 1,
										b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(b_dist[i][j + 1],
										b_dist[i][j]);
							}
						} else if (j == w - 1) {
							if (b_dist[i + 1][j] != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(b_dist[i + 1][j] + 1,
										b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(b_dist[i + 1][j],
										b_dist[i][j]);
							}
						} else {
							final int min = Math.min(b_dist[i + 1][j],
									b_dist[i][j + 1]);
							if (min != Integer.MAX_VALUE) {
								b_dist[i][j] = Math.min(min + 1, b_dist[i][j]);
							} else {
								b_dist[i][j] = Math.min(min, b_dist[i][j]);
							}
						}

						if(b_dist[i][j] != cur){
							updated = true;
						}
					}
				}

				if(!updated){
					break;
				}
			}

			int b_count = 0;
			int w_count = 0;
			for (int i = 0; i < h; i++) {
				for (int j = 0; j < w; j++) {
					final int w_d = w_dist[i][j] == Integer.MAX_VALUE ? Integer.MIN_VALUE
							: w_dist[i][j];
					final int b_d = b_dist[i][j] == Integer.MAX_VALUE ? Integer.MIN_VALUE
							: b_dist[i][j];

					if (w_d > 0 && b_d > 0) {
						continue;
					} else if (w_d > 0) {
						w_count++;
					} else if (b_d > 0) {
						b_count++;
					}
				}
			}

			System.out.println(b_count + " " + w_count);
		}
	}

import java.io.InputStream;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Comparator;
import java.util.Deque;
import java.util.InputMismatchException;
import java.util.NoSuchElementException;
import java.math.BigInteger;

public class Main{

static PrintWriter out;
static InputReader ir;

static void solve(){
 for(;;){
  int w=ir.nextInt();
  int h=ir.nextInt();
  if(w==0&&h==0) return;
  char[][] map=ir.nextCharMap(h,w);
  int[][] cnd=new int[h][w];
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(map[i][j]!='.') bfs(i,j,map[i][j]=='B'?1:2,map,cnd);
   }
  }
  int bl=0,wh=0;
  for(int i=0;i<h;i++){
   for(int j=0;j<w;j++){
    if(cnd[i][j]==1) bl++;
    if(cnd[i][j]==2) wh++;
   }
  }
  out.println(bl+" "+wh);
 }
}

static final int[] dd=new int[]{0-1,0,1};

public static boolean in(int x,int y,int h,int w){
 return (x>=0&&y>=0&&x<h&&y<w);
}

public static void bfs(int x,int y,int m,char[][] map,int[][] cnd){
 int h=map.length,w=map[0].length;
 Deque<int[]> que=new ArrayDeque<>();
 for(int i=0;i<4;i++){
  int nx=x+dd[i],ny=y+dd[i^1];
  if(in(nx,ny,h,w)&&map[nx][ny]=='.') que.offerLast(new int[]{nx,ny});
 }
 while(!que.isEmpty()){
  int[] p=que.pollFirst();
  int px=p[0],py=p[1];
  cnd[px][py]|=m;
  for(int i=0;i<4;i++){
   int nx=px+dd[i],ny=py+dd[i^1];
   if(in(nx,ny,h,w)&&map[nx][ny]=='.'&&(cnd[nx][ny]&m)==0) que.offerLast(new int[]{nx,ny});
  }
 }
}

