static class InputReader {
 private InputStream in;
 private byte[] buffer=new byte[1024];
 private int curbuf;
 private int lenbuf;

 public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

 public boolean hasNextByte() {
  if(curbuf>=lenbuf){
   curbuf= 0;
   try{
    lenbuf=in.read(buffer);
   }catch(IOException e) {
    throw new InputMismatchException();
   }
   if(lenbuf<=0) return false;
  }
  return true;
 }

 private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

 private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

 private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

 public boolean hasNext(){skip(); return hasNextByte();}

 public String next(){
  if(!hasNext()) throw new NoSuchElementException();
  StringBuilder sb=new StringBuilder();
  int b=readByte();
  while(!isSpaceChar(b)){
   sb.appendCodePoint(b);
   b=readByte();
  }
  return sb.toString();
 }

 public int nextInt() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  int res=0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public long nextLong() {
  if(!hasNext()) throw new NoSuchElementException();
  int c=readByte();
  while (isSpaceChar(c)) c=readByte();
  boolean minus=false;
  if (c=='-') {
   minus=true;
   c=readByte();
  }
  long res = 0;
  do{
   if(c<'0'||c>'9') throw new InputMismatchException();
   res=res*10+c-'0';
   c=readByte();
  }while(!isSpaceChar(c));
  return (minus)?-res:res;
 }

 public double nextDouble(){return Double.parseDouble(next());}

 public int[] nextIntArray(int n){
  int[] a=new int[n];
  for(int i=0;i<n;i++) a[i]=nextInt();
  return a;
 }

 public long[] nextLongArray(int n){
  long[] a=new long[n];
  for(int i=0;i<n;i++) a[i]=nextLong();
  return a;
 }

 public char[][] nextCharMap(int n,int m){
  char[][] map=new char[n][m];
  for(int i=0;i<n;i++) map[i]=next().toCharArray();
  return map;
 }
}
}

import java.util.Scanner;
public class Main
{
	static int field[][];
	static int white=0,black=0,cnt,fb,fw;
	static int W,H;
	public static void main(String[] args) 
	{
		Scanner in=new Scanner(System.in);
		for(;;)
		{
			W=in.nextInt();
			H=in.nextInt();
			if((W|H)==0)
				return;
			field=new int[H][W];
			for(int i=0;i<H;i++)
			{
				String st=in.next();
				for(int j=0;j<W;j++)
				{
					if(st.charAt(j)=='.')
						field[i][j]=0;
					else if(st.charAt(j)=='W')
						field[i][j]=1;
					else if(st.charAt(j)=='B')
						field[i][j]=2;
				}
			}
			black=0;
			white=0;
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					cnt=0;
					fb=0;
					fw=0;
					if(field[i][j]==0)
						solve(i,j);
				}
			}
			System.out.println(black+" "+white);
		}	
	}
	public static void solve(int h,int w)
	{
		field[h][w]=3;
		cnt++;
		if(h-1>=0&&field[h-1][w]==1)
			fw=1;
		if(h-1>=0&&field[h-1][w]==2)
			fb=1;
		if(h+1<H&&field[h+1][w]==1)
			fw=1;
		if(h+1<H&&field[h+1][w]==2)
			fb=1;
		if(w-1>=0&&field[h][w-1]==1)
			fw=1;
		if(w-1>=0&&field[h][w-1]==2)
			fb=1;
		if(w+1<W&&field[h][w+1]==1)
			fw=1;
		if(w+1<W&&field[h][w+1]==2)
			fb=1;
		if(h-1>=0&&w-1>=0&&field[h-1][w-1]==1)
			fw=1;
		if(h-1>=0&&w-1>=0&&field[h-1][w-1]==2)
			fb=1;
		if(h-1>=0&&w+1<W&&field[h-1][w+1]==1)
			fw=1;
		if(h-1>=0&&w+1<W&&field[h-1][w+1]==2)
			fb=1;
		if(h+1<H&&w+1<W&&field[h+1][w+1]==1)
			fw=1;
		if(h+1<H&&w+1<W&&field[h+1][w+1]==2)
			fb=1;
		if(h+1<H&&w-1>=0&&field[h+1][w-1]==1)
			fw=1;
		if(h+1<H&&w-1>=0&&field[h+1][w-1]==2)
			fb=1;

		if(h-1>=0&&field[h-1][w]==0)
			solve(h-1,w);
		if(h+1<H&&field[h+1][w]==0)
			solve(h+1,w);
		if(w-1>=0&&field[h][w-1]==0)
			solve(h,w-1);
		if(w+1<W&&field[h][w+1]==0)
			solve(h,w+1);

		if(fw==1&&fb==0)
		{
			white+=cnt;
			cnt=0;
			fw=0;
			fb=0;
			return;
		}
		else if(fw==0&&fb==1)
		{
			black+=cnt;
			cnt=0;
			fw=0;
			fb=0;
			return;
		}
		else if(fw==1&&fb==1)
		{
			cnt=0;
			return;
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;

namespace V2014{
    public class Program{
        readonly int W;
        readonly int H;
        readonly string[] A;

        readonly int[][] _grid;
        readonly int[] _dx = {1, 0, -1, 0};
        readonly int[] _dy = {0, 1, 0, -1};

        bool IsInside(int x, int y){
            return 0 <= x && x < W && 0 <= y && y < H;
        }

        public static void Main(string[] args){
            new Program().Nyan();
        }

        void Nyan() { }

        Program(){
            while(true){
                var sr = new StreamReader();
                W = sr.Next<int>();
                H = sr.Next<int>();
                A = sr.Next<string>(H);
                if(W + H == 0) return;

                _grid = new int[H, W].ToJaggedArray();
                for(var i = 0; i < H; i++){
                    for(var j = 0; j < W; j++){
                        _grid[i][j] = ".BW".IndexOf(A[i][j]);
                    }
                }
                Solve();
            }
        }

        void Solve(){
            var dic = new Dictionary<int, int>{{-1, 0}, {1, 0}};
            for(var i = 0; i < H; i++){
                for(var j = 0; j < W; j++){
                    var count = Bfs(j, i);
                    if(dic.ContainsKey(Math.Sign(count))){
                        dic[Math.Sign(count)] += Math.Sign(count) * count;
                    }
                }
            }
            Console.WriteLine(string.Join(" ", dic.Values.Select(x => x.ToString()).ToArray()));
        }

        int Bfs(int x, int y){
            if(_grid[y][x] != 0) return 0;

            var count = 1;
            _grid[y][x] = int.MinValue;
            var hs = new HashSet<int>();

            var que = new Queue<int>();
            que.Enqueue(x);
            que.Enqueue(y);

            while(que.Count > 0){
                var cx = que.Dequeue();
                var cy = que.Dequeue();
                for(var i = 0; i < _dx.Length; i++){
                    var nx = cx + _dx[i];
                    var ny = cy + _dy[i];
                    if(IsInside(nx, ny) && _grid[ny][nx] != int.MinValue){
                        if(_grid[ny][nx] == 0){
                            que.Enqueue(nx);
                            que.Enqueue(ny);
                            _grid[ny][nx] = int.MinValue;
                            count++;
                        } else{
                            hs.Add(_grid[ny][nx]);
                        }
                    }
                }
            }
            if(hs.Count != 1) return 0;
            return (2 * hs.Single() - 3) * count;
        }
    }

    public static class ExMethod{
        public static T[][] ToJaggedArray<T>(this T[,] src){
            var x = src.GetLength(1);
            var y = src.GetLength(0);
            var ret = new T[y][];

            for(var i = 0; i < y; i++){
                ret[i] = new T[x];
                for(var j = 0; j < x; j++){
                    ret[i][j] = src[i, j];
                }
            }

            return ret;
        }
    }

    public class StreamReader{
        private readonly char[] _c = {' '};
        private int _index = -1;
        private string[] _input = new string[0];
        private readonly System.IO.StreamReader _sr = new System.IO.StreamReader(Console.OpenStandardInput());

        public T Next<T>(){
            if(_index == _input.Length - 1){
                _index = -1;
                while(true){
                    string rl = _sr.ReadLine();
                    if(rl == null){
                        if(typeof(T).IsClass) return default(T);
                        return (T)typeof(T).GetField("MinValue").GetValue(null);
                    }
                    if(rl != ""){
                        _input = rl.Split(_c, StringSplitOptions.RemoveEmptyEntries);
                        break;
                    }
                }
            }
            return (T)Convert.ChangeType(_input[++_index], typeof(T), System.Globalization.CultureInfo.InvariantCulture);
        }

        public T[] Next<T>(int x){
            var ret = new T[x];
            for(var i = 0; i < x; ++i) ret[i] = Next<T>();
            return ret;
        }

        public T[][] Next<T>(int y, int x){
            var ret = new T[y][];
            for(var i = 0; i < y; ++i) ret[i] = Next<T>(x);
            return ret;
        }
    }
}

