namespace V2014{
    public class Program{
        readonly int W;
        readonly int H;
        readonly string[] A;

        readonly int[][] _grid;
        readonly int[] _dx = {1, 0, -1, 0};
        readonly int[] _dy = {0, 1, 0, -1};

        bool IsInside(int x, int y){
            return 0 <= x && x < W && 0 <= y && y < H;
        }

        public static void Main(string[] args){
            new Program();
        }

        Program(){
            while(true){
                var sr = new StreamReader();
                W = sr.Next<int>();
                H = sr.Next<int>();
                A = sr.Next<string>(H);
                if(W + H == 0) return;

                _grid = new int[H, W].ToJaggedArray();
                for(var i = 0; i < H; i++){
                    for(var j = 0; j < W; j++){
                        _grid[i][j] = ".BW".IndexOf(A[i][j]);
                    }
                }
                //Solve();
            }
        }

        void Solve(){
            var dic = new Dictionary<int, int>{{-1, 0}, {1, 0}};
            for(var i = 0; i < H; i++){
                for(var j = 0; j < W; j++){
                    var count = Bfs(j, i);
                    if(dic.ContainsKey(Math.Sign(count))){
                        dic[Math.Sign(count)] += Math.Sign(count) * count;
                    }
                }
            }
            Console.WriteLine(string.Join(" ", dic.Values.Select(x => x.ToString()).ToArray()));
        }

        int Bfs(int x, int y){
            if(_grid[y][x] != 0) return 0;

            var count = 1;
            _grid[y][x] = int.MinValue;
            var hs = new HashSet<int>();

            var que = new Queue<int>();
            que.Enqueue(x);
            que.Enqueue(y);

            while(que.Count > 0){
                var cx = que.Dequeue();
                var cy = que.Dequeue();
                for(var i = 0; i < _dx.Length; i++){
                    var nx = cx + _dx[i];
                    var ny = cy + _dy[i];
                    if(IsInside(nx, ny) && _grid[ny][nx] != int.MinValue){
                        if(_grid[ny][nx] == 0){
                            que.Enqueue(nx);
                            que.Enqueue(ny);
                            _grid[ny][nx] = int.MinValue;
                            count++;
                        } else{
                            hs.Add(_grid[ny][nx]);
                        }
                    }
                }
            }
            if(hs.Count != 1) return 0;
            return (2 * hs.Single() - 3) * count;
        }
    }

