using System;
using System.Collections.Generic;
using System.ComponentModel.Design;

class Program {
  static int n, m;
  static char[][] map;
  private static readonly int[] dy = new[] {1, -1, 0, 0};
  private static readonly int[] dx = new[] {0, 0, 1, -1};
  public static int Main() {
    Scanner cin = new Scanner();
    while (true)
    {
      n = cin.nextInt();
      m = cin.nextInt();
      if (n == 0 && m == 0)
      {
        break;
      }
      map = new char[m][];
      for (int i = 0; i < m; i++)
      {
        map[i] = cin.next().ToCharArray();
      }
      int b = 0, w = 0;
      for (int i = 0; i < m; i++)
      {
        for (int j = 0; j < n; j++)
        {
          if (map[i][j] == '.')
          {
            char color = getColor(j, i);
            if (color == 'B')
            {
              b += dfs(j, i);
            }
            if (color == 'W')
            {
              w += dfs(j, i);
            }
          }
        }
      }
      Console.WriteLine("{0} {1}", b, w);
    }
    return 0;
  }

  private static char getColor(int nowx, int nowy)
  {
    if (map[nowy][nowx] != '.')
    {
      return map[nowy][nowx];
    }
    map[nowy][nowx] = 'C';
    char res = '.';
    for (int i = 0; i < 4; i++)
    {
      if (nowy + dy[i] < 0 || nowy + dy[i] == m || nowx + dx[i] < 0 || nowx + dx[i] == n || map[nowy + dy[i]][nowx + dx[i]] == 'C')
      {
        continue;
      }
      char c = getColor(nowx + dx[i], nowy + dy[i]);
      if ((res != '.' && res != c && c != '.') || c == 'e')
      {
        res = 'e';
      }
      else
      {
        res = c;
      }
    }
    return res;
  }

  private static int dfs(int nowx, int nowy)
  {
    if (map[nowy][nowx] != 'C')
    {
      return 0;
    }
    map[nowy][nowx] = 'S';
    int res = 1;
    for (int i = 0; i < 4; i++)
    {
      if (nowy + dy[i] < 0 || nowy + dy[i] == m || nowx + dx[i] < 0 || nowx + dx[i] == n) {
        continue;
      }
      res += dfs(nowx + dx[i], nowy + dy[i]);
    }
    return res;
  }

}

class Scanner {
  string[] s;
  int i;

  char[] cs = new char[] { ' ' };

  public Scanner() {
    s = new string[0];
    i = 0;
  }

  public string next() {
    if (i < s.Length) return s[i++];
    do {
      s = Console.ReadLine().Split(cs, StringSplitOptions.RemoveEmptyEntries);
    } while ((s.Length == 1 && s[0] == "") || s.Length == 0);
    i = 0;
    return s[i++];
  }

  public int nextInt() {
    return int.Parse(next());
  }

  public long nextLong() {
    return long.Parse(next());
  }

  public double nextDouble() {
    return double.Parse(next());
  }

}

using System;

public class Hello
{
    public static void Main()
    {
        var dx = new int[] { 0, 1, 0, -1 };
        var dy = new int[] { 1, 0, -1, 0 };
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var w = int.Parse(line[0]);
            var h = int.Parse(line[1]);
            if (w == 0 && h == 0) break;
            var map1 = new int[h, w]; var map2 = new int[h, w];
            for (int i = 0; i < h; i++)
            {
                var s = Console.ReadLine().Trim();
                for (int j = 0; j < w; j++)
                    if (s[j] == 'B') { map1[i, j] = 1; map2[i, j] = 1; }
                    else if (s[j] == 'W') { map1[i, j] = 2; map2[i, j] = 2; }
            }
            var found = false;
            for (int i = 0; i < h; i++)
                for (int j = 0; j < w; j++)
                {
                    if (map1[i, j] == 1) { goDfs(map1, dx, dy, i, j); found = true; }
                    if (map2[i, j] == 2) { goDfs(map2, dx, dy, i, j); found = true; }
                }
            if (!found) { Console.WriteLine("0 0");  goto end; }
            var wcount = 0; var bcount = 0;
            for (int i = 0; i < h; i++)
                for (int j = 0; j < w; j++)
                {
                    if (map1[i, j] == 0) wcount++;
                    if (map2[i, j] == 0) bcount++;
                }
            Console.WriteLine("{0} {1}", bcount, wcount);
            end:;
        }
    }
    public static void goDfs(int[,] map, int[] dx, int[] dy, int x, int y)
    {
        var h = map.GetLength(0); var w = map.GetLength(1);
        if (map[x, y] == 0) map[x, y] = 10;
        for (int i = 0; i < 4; i++)
        {
            var nx = x + dx[i];
            var ny = y + dy[i];
            if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx, ny] == 0)
                goDfs(map, dx, dy, nx, ny);
        }
    }
}

