        var dx = new int[] { 0, 1, 0, -1 };
        var dy = new int[] { 1, 0, -1, 0 };
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var w = int.Parse(line[0]);
            var h = int.Parse(line[1]);
            if (w == 0 && h == 0) break;
            var map1 = new int[h, w]; var map2 = new int[h, w];
            for (int i = 0; i < h; i++)
            {
                var s = Console.ReadLine().Trim();
                for (int j = 0; j < w; j++)
                    if (s[j] == 'B') { map1[i, j] = 1; map2[i, j] = 1; }
                    else if (s[j] == 'W') { map1[i, j] = 2; map2[i, j] = 2; }
            }
            for (int i = 0; i < h; i++)
                for (int j = 0; j < w; j++)
                {
                    if (map1[i, j] == 1) goDfs(map1, dx, dy, i, j);
                    if (map2[i, j] == 2) goDfs(map2, dx, dy, i, j);
                }
            var wcount = 0; var bcount = 0;
            for (int i = 0; i < h; i++)
                for (int j = 0; j < w; j++)
                {
                    if (map1[i, j] == 0) wcount++;
                    if (map2[i, j] == 0) bcount++;
                }
            Console.WriteLine("{0} {1}", bcount, wcount);
        }
    }
    public static void goDfs(int[,] map, int[] dx, int[] dy, int x, int y)
    {
        var h = map.GetLength(0); var w = map.GetLength(1);
        if (map[x, y] == 0) map[x, y] = 10;
        for (int i = 0; i < 4; i++)
        {
            var nx = x + dx[i];
            var ny = y + dy[i];
            if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx, ny] == 0)
                goDfs(map, dx, dy, nx, ny);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;

namespace V2014{
    public class Program{
        readonly int W;
        readonly int H;
        readonly string[] A;

        readonly int[][] _grid;
        readonly int[] _dx = {1, 0, -1, 0};
        readonly int[] _dy = {0, 1, 0, -1};

        bool IsInside(int x, int y){
            return 0 <= x && x < W && 0 <= y && y < H;
        }

        public static void Main(string[] args){
            var prg = new Program();
        }

        Program(){
            while(true){
                var sr = new StreamReader();
                W = sr.Next<int>();
                H = sr.Next<int>();
                A = sr.Next<string>(H);
                if(W + H == 0) return;

                _grid = new int[H, W].ToJaggedArray();
                for(var i = 0; i < H; i++){
                    for(var j = 0; j < W; j++){
                        _grid[i][j] = ".BW".IndexOf(A[i][j]);
                    }
                }
                Solve();
            }
        }

        void Solve(){
            var dic = new Dictionary<int, int>{{-1, 0}, {1, 0}};
            for(var i = 0; i < H; i++){
                for(var j = 0; j < W; j++){
                    var count = Bfs(j, i);
                    if(dic.ContainsKey(Math.Sign(count))){
                        dic[Math.Sign(count)] += Math.Sign(count) * count;
                    }
                }
            }
            Console.WriteLine(string.Join(" ", dic.Values.Select(x => x.ToString()).ToArray()));
        }

        int Bfs(int x, int y){
            if(_grid[y][x] != 0) return 0;

            var count = 1;
            _grid[y][x] = int.MinValue;
            var hs = new HashSet<int>();

            var que = new Queue<int>();
            que.Enqueue(x);
            que.Enqueue(y);

            while(que.Count > 0){
                var cx = que.Dequeue();
                var cy = que.Dequeue();
                for(var i = 0; i < _dx.Length; i++){
                    var nx = cx + _dx[i];
                    var ny = cy + _dy[i];
                    if(IsInside(nx, ny) && _grid[ny][nx] != int.MinValue){
                        if(_grid[ny][nx] == 0){
                            que.Enqueue(nx);
                            que.Enqueue(ny);
                            _grid[ny][nx] = int.MinValue;
                            count++;
                        } else{
                            hs.Add(_grid[ny][nx]);
                        }
                    }
                }
            }
            if(hs.Count != 1) return 0;
            return (2 * hs.Single() - 3) * count;
        }
    }

    public static class ExMethod{
        public static T[][] ToJaggedArray<T>(this T[,] src){
            var x = src.GetLength(1);
            var y = src.GetLength(0);
            var ret = new T[y][];

            for(var i = 0; i < y; i++){
                ret[i] = new T[x];
                for(var j = 0; j < x; j++){
                    ret[i][j] = src[i, j];
                }
            }

            return ret;
        }
    }

    public class StreamReader{
        private readonly char[] _c = {' '};
        private int _index = -1;
        private string[] _input = new string[0];
        private readonly System.IO.StreamReader _sr = new System.IO.StreamReader(Console.OpenStandardInput());

        public T Next<T>(){
            if(_index == _input.Length - 1){
                _index = -1;
                while(true){
                    string rl = _sr.ReadLine();
                    if(rl == null){
                        if(typeof(T).IsClass) return default(T);
                        return (T)typeof(T).GetField("MinValue").GetValue(null);
                    }
                    if(rl != ""){
                        _input = rl.Split(_c, StringSplitOptions.RemoveEmptyEntries);
                        break;
                    }
                }
            }
            return (T)Convert.ChangeType(_input[++_index], typeof(T), System.Globalization.CultureInfo.InvariantCulture);
        }

        public T[] Next<T>(int x){
            var ret = new T[x];
            for(var i = 0; i < x; ++i) ret[i] = Next<T>();
            return ret;
        }

        public T[][] Next<T>(int y, int x){
            var ret = new T[y][];
            for(var i = 0; i < y; ++i) ret[i] = Next<T>(x);
            return ret;
        }
    }
}

