const DPos = [Pos(1,0),Pos(0,1),Pos(-1,0),Pos(0,-1)];
struct Pos{ 
	int x,y,z;
	this(int x_ =0,int y_=0,int z_=0){x=x_;y=y_;z=z_;}
	this(ulong x_ =0,ulong y_=0,ulong z_=0){x=cast(int)x_;y=cast(int)y_;z=cast(int)z_;}
	bool inRange(int w,int h,int Z=1){
		return x>=0 && y >= 0 && z >=0 && x < w && y < h && z < Z;
	}
	bool inRange(ulong w,ulong h,ulong Z=1){return inRange(cast(int)w,cast(int)h,cast(int)Z);}
	Pos opBinary(string op)(Pos rhs) {
		return Pos ( mixin( "x"~op~"rhs.x" ),
					 mixin( "y"~op~"rhs.y" ),
					mixin( "z"~op~"rhs.z" ));
	}
	void opOpAssign(string op)(Pos lhs) {
		mixin("x"~op~"=lhs.x;");
		mixin("y"~op~"=lhs.y;");
		mixin("z"~op~"=lhs.z;");
	}
}

struct Matrix(T){
	import std.conv;
	private T[][] data;
	this(int w,int h){ data = new T[][](w,h);}
	this(ref T[][] data_){ data = data_;}
	T opIndexAssign(T v, size_t x, size_t y){return data[x][y] = v;}
	T opIndexAssign(T v, Pos p){return opIndexAssign(v,p.x,p.y);}
	ref T opIndex(size_t x, size_t y){return data[x][y];}
	ref T opIndex(Pos p){return opIndex(p.x,p.y);}
	ref T opCall(Pos p){return data[p.x][p.y];}
	@property auto W()const{return data.length;}	
	@property auto H()const{return data[0].length;}	
