                        continue
                    elif field[y][x]=='B':
                        check.add('B')
                        continue
                    else:
                        continue
                    if 0<=y+1<h and 0<=x<w:
                        q.put((y+1,x))
                    if 0<=y-1<h and 0<=x<w:
                        q.put((y-1,x))
                    if 0<=y<h and 0<=x+1<w:
                        q.put((y,x+1))
                    if 0<=y<h and 0<=x-1<w:
                        q.put((y,x-1))
                if len(check)==2:
                    pass
                elif 'B' in check:
                    B+=d
                elif 'W' in check:
                    W+=d
