from collections import deque

while True:

    W,H = map(int,input().split())

    if W == 0 and H == 0:
        break

    a = []

    for i in range(H):

        A = list(input())
        a.append(A)

    q = deque([])

    for i in range(H):
        for j in range(W):

            if a[i][j] in ["W","B"]:
                q.append([i,j])

    while len(q) > 0:

        now = q.pop()
        nx = now[0]
        ny = now[1]

        if a[nx][ny] in[ "W","X" ]:

            if nx - 1 >= 0 and a[nx-1][ny] == ".":
                a[nx-1][ny] = "X"
