def ds4(here, lb, ub, field, label):
	if not field[here[1]][here[0]][label]:
		field[here[1]][here[0]][label] = True

		if lb[0] < here[0]:
			ds4((here[0]-1, here[1]), lb, ub, field, label)

		if lb[1] < here[1]:
			ds4((here[0], here[1]-1), lb, ub, field, label)

		if ub[0] > here[0]:
			ds4((here[0]+1, here[1]), lb, ub, field, label)

		if ub[1] > here[1]:
			ds4((here[0], here[1]+1), lb, ub, field, label)

