class Area:
    def __init__(self,m,x,y):
        self.m = m
        self.f_W = False
        self.f_B = False
        self.side = 0
        self.count = 0
        self.standardx = x
        self.standardy = y
        self.check(x,y)
        self.getSide()
    def check(self,x,y):
        if(y<0 or y >= len(m) or x<0 or x>=len(m[0])):
            return
        c = self.m[y][x]
        if(c!=0):
            if(c==1):
                self.f_B=True
            if(c==2):
                self.f_W=True
            return
        else:
            if(c==0):
                self.m[y][x]=3
                self.count += 1
                self.check(x-1,y)
                self.check(x+1,y)
                self.check(x,y-1)
                self.check(x,y+1)
    def getSide(self):
        if(self.f_B and self.f_W):
            self.side = 0
        elif(self.f_B):
            self.side = 1
        elif(self.f_W):
            self.side = 2
        else:
            self.side = 0
        return self.side




