#include<iostream>
#include<algorithm>
#include<vector>
using namespace std;

int k,ans;
vector<int> dif;
const int M = 10;

void rec(int d, int p){
  if(p==k){
    ans = min(ans,d);
    return;
  }
  if(d>=ans)return;

  for(int i=1;i<=k;i++){
    if(p+i-1>k)return;
    vector<int> tmp = dif;
    for(int j=p+i-1;j>=p;j--){
      dif[j] -= dif[p];
      dif[j] = (dif[j]%M +M)%M;
    }
    int np = p;
    while(np<k && !dif[np])np++;
    rec(d+1,np);
    dif = tmp;
  }
}

int main(){
  while(cin >> k,k){
    dif.resize(k);

    string s,t;
    cin >> s >> t;
    for(int i=0;i<k;i++){
      dif[i] = (( (int)(t[i]-s[i]) ) % M + M ) % M;
    }

    ans = 10;
    rec(0,0);
    cout << ans << endl;
  }
}

