int bfs(string& S, string& T)
{
  queue< string > que;
  map< string, int > v;
  que.push(S);
  v[S] = 0;
  while(!que.empty()) {
    auto& p = que.front();
    if(p == T) return(v[p]);
    for(int i = 0; i < p.size(); i++) {
      for(int j = i; j < p.size(); j++) {
        string q = p;
        for(int l = 1; l < 10; l++) {
          for(int k = i; k <= j; k++) (q[k] += 1) %= 10; 
          if(v.find(q) == v.end()) {
            v[q] = v[p] + 1;
            que.push(q);
          }
        }
      }
    }
    que.pop();
  }
}
