int compute_turn_count(int src,int dst){
  for(int i=0;i<10;i++){
    if((src + i) % 10 == dst) return i;
  }
  return -1;
}

bool has_equal(const string& current_state,const string& unlocked_state,int first,int last){
  for(int i=first;i<=last;i++){
    if(current_state[i] == unlocked_state[i]) return true;
  }
  return false;
}

string turn(const string& str,int first,int last,int turn_count){
  string next = str;
  for(int i=first;i<=last;i++){
    next[i] = ((str[i] - '0' + turn_count) % 10) + '0';
  }
  return next;
}

class State{
public:
  string dial;
  int cost;
  State(string _d,int _c) : dial(_d),cost(_c) {}
  State() : dial(""),cost(0) {}

  bool operator <(const State& s) const{
    return cost < s.cost;
  }

  bool operator >(const State& s) const{
    return cost > s.cost;
  }
};

int main(){
  int total_dials;
  while(~scanf("%d",&total_dials)){
    if(total_dials == 0) break;

    string initial_state,unlocked_state;
    cin >> initial_state >> unlocked_state;
    map<string,char> from_start_cost;
    map<string,char> from_goal_cost;

    from_start_cost[initial_state] = 0;
    from_goal_cost[unlocked_state] = 0;

    priority_queue<State,vector<State>,greater<State> > que;
    que.push(State(initial_state,0));

    while(!que.empty()){
      State s = que.top();
      que.pop();

      if(s.cost >= 5) continue;
      for(int first=0;first<s.dial.size();first++){
	for(int last=first;last<s.dial.size();last++){
	  if(has_equal(s.dial,unlocked_state,first,last)) continue;

	  for(int turn_count=1;turn_count<=9;turn_count++){
	    string next = turn(s.dial,first,last,turn_count);
	    if(from_start_cost.find(next) == from_start_cost.end()){
	      from_start_cost[next] = s.cost + 1;
	      // if(next == unlocked_state) goto found;
	      que.push(State(next,s.cost+1));
	    }
	  }
	}
      }
    }

    priority_queue<State,vector<State>,greater<State> > que2;
    que2.push(State(unlocked_state,0));

    while(!que2.empty()){
      State s = que2.top();
      que2.pop();

      if(s.cost >= 5) continue;
      for(int first=0;first<s.dial.size();first++){
	for(int last=first;last<s.dial.size();last++){
	  if(has_equal(s.dial,unlocked_state,first,last)) continue;

	  for(int turn_count=1;turn_count<=9;turn_count++){
	    string next = turn(s.dial,first,last,turn_count);
	    if(from_goal_cost.find(next) == from_goal_cost.end()){
	      from_goal_cost[next] = s.cost + 1;
	      // if(next == unlocked_state) goto found;
	      que.push(State(next,s.cost+1));
	    }
	  }
	}
      }
    }

    int res = INF;
    for(map<string,char>::iterator it = from_start_cost.begin();
	it != from_start_cost.end();
	it++){
      if(it->first == unlocked_state){
	res = it->second;
	break;
      }
      res = min(res,it->second + from_goal_cost[it->first]);
    }
    printf("%d\n",res);
  }
}

