const int INF = 20;

int dfs(int index, vector<int>& ds) {
    if (index == N) return 0;
    int ret = INF;
    int d = ds[index];
    if (d == 0) { return dfs(index + 1, ds); }
    vector<int> backup = ds;
    for (int i = index; i < N; i++) {
        ds[i] = (ds[i] + 10 - d) % 10;
        ret = min(ret, dfs(index + 1, ds) + 1);
    }
    ds = backup;
    return ret;
}

