int bfs(int k, string s, string g){
	map<string, int> d;
	d[s] = 0;
	queue<string> q;
	q.push(s);

	int res = 0;
	while( !q.empty() ){
		string now = q.front(); q.pop();
		int cost = d[now];
		if( now == g ) return cost;

		int pos = 0, diff;
		for(int i = 0; i < k; i++){
			if( now[i] != g[i] ){
				pos = i;
				diff = (g[i] - '0') - (now[i] - '0');
				break;
			}
		}
		for(int i = pos; i < k; i++){
			string next = now;

			for(int j = pos; j <= i; j++){
				next[j] += diff;
				while( '9' < next[j] ) next[j] -= 10;
				while( next[j] < '0' ) next[j] += 10;
			}
			if( !d.count(next) ){
				d[next] = cost + 1;
				q.push(next);
			}
		}
	}
	return -1;
}

