    for(int i=0; i<K; i++) {
        ret = (ret << 1) | (S[i] == T[i]);
    }
    return ret;
}

void solve() {
    mp[s] = 0;
    queue<string> que;
    que.push(s);
    while(que.size()) {
        string cur = que.front(); que.pop();
        // printf("cur = %s (%d)\n", cur.c_str(), mp[cur]);
        if(cur == t) {
            cout << mp[cur] << endl;
            return;
        }

        for(int l=0; l<K; l++) {
            if(cur[l] != t[l]) {
                for(int b=1; b<=K-l; b++) {
                    int r = l+b;
                    for(int rot=1; rot<10; rot++) {
                        string nxt = cur;
                        int nxt_pos = INF;
                        for(int i=l; i<r; i++) {
                            int val = ((nxt[i] - '0') + rot) % 10;
                            nxt[i] = (char)('0' + val);
                            if(nxt[i] != t[i]) nxt_pos = min(nxt_pos, i);
                        }
                        if(nxt_pos == l) continue;
                        if(mp.count(nxt) && mp[nxt] <= mp[cur] + 1) continue;
                        mp[nxt] = mp[cur] + 1;
                        que.push(nxt);
                    }
                }
                break;
            }
        }
    }
}

