const int M = 10;

void rec(int d, int p){
  if(p==k){
    ans = min(ans,d);
    return;
  }
  if(d>=ans)return;

  for(int i=k;i>=1;i--){
    if(p+i-1>=k)continue;
    vector<int> tmp = dif;
    for(int j=p+i-1;j>=p;j--){
      dif[j] -= dif[p];
      dif[j] = (dif[j]%M +M)%M;
    }
    int np = p;
    while(np<k && !dif[np])np++;
    rec(d+1,np);
    dif = tmp;
  }
}

