			if(s[i] < 0)
				s[i] += 10;
		}
		rep(i, k-1){
			if(s[i] == s[i+1]){
				reep(j, i+1, k-1)
					s[j] = s[j+1];
				--k;
				--i;
			}
		}

		vi pos[10];
		rep(i, k) pos[s[i]].push_back(i);

		int dp[10][10] = {{0}};
		int zero[10][10] = {{0}};
		rep(i, k){
			for(int j=i; j>=0; --j){
				if(j==i)
					zero[j][i] = !s[j];
