					dp[j][i] = dp[j+1][i];
					zero[j][i] = zero[j+1][i] + !s[j];
					zero[j][i] += !s[j];

					int left = distance(pos[s[j]].begin(), lower_bound(pos[s[j]].begin(), pos[s[j]].end(), j));
					int right = distance(pos[s[j]].begin(), upper_bound(pos[s[j]].begin(), pos[s[j]].end(), i));

					if(left+1 < right){
						int tmp = dp[pos[s[j]][right-1]+1][i] + right - left - 1;
						int z = !s[j] + zero[pos[s[j]][right-1]+1][i];
						reep(l, left, right-1){
