void calc(int k){
  char s1[11],s2[11];
  int dif[11];

  scanf("%s%s",s1,s2);

  for(int i=0;i<k;i++){
    dif[i] = (s1[i]+10-s2[i])%10;
  }

  int dp[10][10][2] = {};

  for(int i=0;i<k;i++){
    for(int j=0;j<k;j++){
      dp[i][j][0] = dp[i][j][1] = INF;
    }
    dp[i][i][0] = dp[i][i][1] = 1;
    if(dif[i]==0) dp[i][i][1] = 0;
  }

  for(int l=0;l<k;l++){
    for(int i=0;i+l<k;i++){

      if(i+l+1 < k){
        if(dif[i+l]==dif[i+l+1]){
          dp[i][i+l+1][0] = min(dp[i][i+l+1][0], dp[i][i+l][0]);
          dp[i][i+l+1][1] = min(dp[i][i+l+1][1], dp[i][i+l][1]);
        }else{
          dp[i][i+l+1][0] = min(dp[i][i+l+1][0], dp[i][i+l][0]+1);
          dp[i][i+l+1][1] = min(dp[i][i+l+1][1], dp[i][i+l][1]+1);
        }
      }

      if(0<=i-1){
        if(dif[i-1]==dif[i]){
          dp[i-1][i+l][0] = min(dp[i-1][i+l][0],dp[i][i+l][0]);
          dp[i-1][i+l][1] = min(dp[i-1][i+l][1],dp[i][i+l][1]);
        }else{
          dp[i-1][i+l][0] = min(dp[i-1][i+l][0],dp[i][i+l][0]+1);
          dp[i-1][i+l][1] = min(dp[i-1][i+l][1],dp[i][i+l][1]+1);
        }
      }

      if(0<=i-1 && i+l+1<k && dif[i-1]==dif[i+l+1]){
        dp[i-1][i+l+1][0] = min(dp[i-1][i+l+1][0],dp[i][i+l][0]+1);
      }

      if(i+l+1 < k){
        for(int j=i;j<=i+l;j++){
          if(dif[j] == dif[i+l+1]){
            dp[i][i+l+1][0] = min(dp[i][i+l+1][0],dp[i][j][0]+dp[j+1][i+l+1][0]);
            dp[i][i+l+1][1] = min(dp[i][i+l+1][1],dp[i][j][1]+dp[j+1][i+l+1][1]);
          }
        }
      }
    }
  }

  printf("%d\n",min(dp[0][k-1][0],dp[0][k-1][1]));
}

