int solve(int n, int array[])
{
	if(n <= 0) return 0;

	int rot = array[0], tmp, min = n+1;
	if(rot == 0) return solve(n-1, array+1);

	for(int i = 1; i <= n; i++){
		for(int j = 0; j < i; j++) array[j] = (array[j] - rot + 10) % 10;
		tmp = solve(n-1, array+1);
		for(int j = 0; j < i; j++) array[j] = (array[j] + rot) % 10;

		if(tmp < min) min = tmp;
	}
	return min + 1;
}


