int N;

int solve(string &s,string &t){
  queue<string> Q;
  map<string,int> dist;
  Q.push(s);
  dist[s] = 0;
  while(!Q.empty()){
    string v = Q.front(); Q.pop();
    if(v == t) return dist[v];
    for(int i = 0 ; i < N ; i++){
      if(v[i] == t[i]) continue;
      for(int j = 1 ; j <= 9 ; j++){
        string next = v;
        for(int k = i ; k < N ; k++){
          next[k] = ((next[k]-'0' + j) % 10) + '0';
          if(dist.find(next) == dist.end()){
            dist[next] = dist[v] + 1;
            Q.push(next);
          }
        }
      }
    }
  }
}

