int compute_dist(int lhs,int rhs){
  if(lhs > rhs){
    //0...rhs...lhs...9
    return (9-lhs + rhs + 1);
  }
  else if(lhs < rhs){
    //0...lhs...rhs...9
    return rhs - lhs;
  }
  return 0;
}

int main(){
