#define MAX_K 10

using namespace std;

struct dial{
	dial():k(0),depth(0),roti(0){
		for(int i = 0; i < MAX_K; ++i)
			d[i] = -1;
	}
	char depth;
	char k;
	char d[MAX_K];
	char roti;
	int diff(const dial &t)const{
		int ret = 0;
		for(int i = 0; i < k; ++i){
			if( d[i] != t.d[i] )
				++ret;
		}
		return ret;
	}
};

int DialLock(const dial &init, const dial &unlock){
	int K = init.k;
	int ret = 0;
	queue<dial> qd;

	qd.push( init );
	while( !qd.empty() ){
		dial now=qd.front();
		qd.pop();

		/*
		for(int i = 0; i < K; ++i){
			cout << now.d[i] << ' ';
		}
		cout << endl;
		*/

		if( 0 == unlock.diff( now ) ){
			ret = now.depth;
			break;
		}

		for(int i = now.roti; i < K; ++i){
			int rot;
			if( now.d[i] != unlock.d[i] ){
				dial t = now;

				++t.depth;
				t.roti = i + 1;
				rot = unlock.d[i] - now.d[i];
				rot += (rot<0?MAX_K:0);

				for(int m = 0; m < K - i; ++m){
					t.d[i+m]=(now.d[i+m]+rot)%MAX_K;
					qd.push( t );
				}

				break;
			}
		}
	}

	return ret;
}

int main(){
	while( true ){
		int k;
		string a,b;
		dial init,unlock;

		cin >> k;
		if( k == 0 )
			break;
		cin >> a >> b;
		init.k = unlock.k = a.length();
		for(unsigned int i = 0; i < a.length(); ++i){
			init.d[i] = a[i] - '0';
			unlock.d[i] = b[i] - '0';
		}
		init.depth=0;

		cout << DialLock( init, unlock ) << endl;
	}
	return 0;
}

