int bfs(string& S, string& T)
{
  queue< string > que;
  unordered_map< string, int > v;
  que.push(S);
  v[S] = 0;
  while(!que.empty()) {
    auto& p = que.front();
    int now = v[p];
    if(p == T) return(now);
    int match = 0;
    while(p[match] == T[match]) ++match;
    int ptrr = (T[match] - p[match] + 10) % 10;
    for(int j = match; j < p.size(); j++) {
      (p[j] += ptrr) %= 10;
      if(v.find(p) == v.end()) {
        v[p] = now + 1;
        que.push(p);
      }
    }
    que.pop();
  }
}
