void solve(vector<int> s,int idx,int cost){
	if(idx == n) ans = cost;
	if(cost >= ans) return;

	if(s[idx] == t[idx]){
		solve(s,idx+1,cost);
	}
	else{
		for(int i=idx;i<n;i++){
			int r = rotate[s[idx]][t[idx]];
			vector<int> next = s;
			for(int j=idx;j<=i;j++) next[j] = (next[j] + 10 + r) % 10;
			solve(next,idx+1,cost+1);
		}
	}
}

