#define REP(i,n) for (int i=0;i<(n);i++)
#define REP2(i,m,n) for (int i=m;i<(n);i++)
typedef long long ll;

int K;
string S, T;

bool ok(vector<int>& a) {
    REP(i, K) if (a[i] != 0) return false;
    return true;
}

bool same(vector<int>& a) {
    REP(i, K) if (a[i] != a[0]) return false;
    return true;
}

bool solve_one() {
    cin >> K;
    if (K == 0) return false;
    cin >> S >> T;
    vector<int> A;
    REP(i, K) A.push_back((S[i] - T[i] + 10) % 10);
    A.push_back(0);

    queue<vector<int>> que;
    que.push(A);
    int ans = 1 << 29;

    while (!que.empty()) {
        vector<int> v = que.front();
        que.pop();
        if (ans <= v.back()) {
            break;
        }
        if (ok(v)) {
            ans = v.back();
            break;
        } else if (same(v)) {
            ans = v.back() + 1;
        }
        REP(i, K-1) if (v[i] != v[i+1]) {
            {
                vector<int> x = v;
                int org = v[i];
                for (int j = i; j >= 0 && v[j] == org; --j) x[j] = v[i+1];
                x.back() += 1;
                que.push(x);
            }
            {
                vector<int> x = v;
                int org = v[i+1];
                for (int j = i+1; j < K && v[j] == org; --j) x[j] = v[i];
                x.back() += 1;
                que.push(x);
            }
        }
    }

    cout << ans << "\n";

    return true;
}

void solve() {
    while (solve_one()) ;
}

