int deleteNumver(vector<int> &d){
    map<int,int> m;
    rep(i,d.size()){
        m[d[i]]++;
    }

    int res = 0;
    if(d.empty()) return res;
    if(m[ d.front() ] == 1) {d.erase(d.begin()); res++; }
    if(d.empty()) return res;
    if(m[ d.back() ] == 1) {d.erase(--d.end()); res++; };

    return res;
}

vector<int> deleteRow(vector<int> d){
    vector<int> res;
    rep(i,d.size()){
        if(res.empty()) res.emplace_back(d[i]);
        else if(res.back() != d[i]) res.emplace_back(d[i]);
    }
    return res;
}

vector<int> deleteZero(vector<int> d){
    vector<int> ret;
    bool u[11] = {0};

    rep(i,d.size()){
        if(d[i] == 0) u[i] = true;
        else break;
    }

    for(int i = d.size() - 1; i >= 0; i--){
        if(d[i] == 0) u[i] = true;
        else break;
    }

    rep(i,d.size()){
        if(not u[i]) ret.emplace_back(d[i]);
    }
    return ret;
}

int main(){
    int n;
    while(cin >> n,n){
        string a,b;
        cin >> a >> b;
        vector<int> d;
        rep(i,n){
            int tmp = b[i] - a[i];
            if(tmp < 0) tmp += 10;
            if(d.empty()) d.emplace_back(tmp);
            else if(d.back() != tmp) d.emplace_back(tmp);
        }

        int cnt = 0;
        int maxi = 0, p;
        while(true){
            int before = d.size();
            //rep(i,d.size()){ cout << d[i] << ' '; }cout << endl;
            d = deleteZero(d);
            d = deleteRow(d);
            cnt += deleteNumver(d);
            //show(cnt)
            //rep(i,d.size()){ cout << d[i] << ' '; }cout << endl;
            if(d.size() == 0) break;

            if(before == d.size()){
                map<int,int> m;
                bool u[11] = {0};
                rep(i,d.size()) if(d[i] == 0) u[i] = true;
                rep(i,d.size()){
                    if(u[i]) continue;
                    m[ d[i] ]++;
                    if(maxi < m[ d[i] ]){
                        maxi = m[ d[i] ];
                        p = d[i];
                    }
                }
                rep(i,d.size()){
                    if(u[i]) continue;
                    if(m[ d[i] ] != maxi) cnt++;
                }
                cnt++;
                break;
            }
        }

        cout << cnt << endl;
    }
}

