typedef long long ll;
int ans;
void solve(string &S, string &T, int pos, int k,int cnt) {
	if (pos == k) {
		ans = min(ans, cnt);
		return;
	}
	int d = (T[pos] - S[pos] + 10) % 10;
	if (d == 0) {
		solve(S, T, pos + 1, k, cnt);
		return;
	}
	for (int i = pos; i < k; i++) {
		(S[i] += d) %= 10;
		solve(S, T, pos + 1, k, cnt + 1);
	}
	for (int i = pos; i < k; i++) (S[i] += 10 - d) %= 10;
}
int main() {
	int k;
	while (cin >> k, k) {
		string S, T;
		cin >> S >> T;
		for (int i = 0; i < k; i++) S[i] -= '0', T[i] -= '0';
		ans = 1 << 30;
		solve(S, T, 0, k, 0);
		cout << ans << endl;
	}
}

