int solve(string s, string t){
  int cnt = 0;
  int size = s.size();
  int dist[10];
  bool match[10];
  string now = s;
  while(now != t){
    //cout << now << endl;
    cnt++;
    for(int i=0;i<size;i++){
      if(now[i] == t[i]) match[i] = true;
      else match[i] = false;
      int num = t[i]-now[i];
      if(num<0) num+=10;
      dist[i] = num;
    }
    /*for(int i=0;i<size;i++){
      cout << dist[i] << ' ';
    }
    cout << endl;*/
    int l = -1;
    int r = size;
    for(int i=0;i<size;i++){
      if(match[i]){
	if(l != -1){
	  r = i;
	}
	continue;
      }
      if(l == -1) l = i;
    }
    //cout << l << ' ' << r << endl;
    for(int i=l;i<r;i++){
      int num = now[i]-'0' + dist[l];
      if(num >= 10) num -= 10;
      now[i] = '0' + num;
    }
    //cout << now << endl;
    //break;
  }
  return cnt;
}

