string A,B;
int k;

int mem[10][10][10],used[10][10][10];

int dfs(int l,int r,int num){
  if(l==r) return (B[l]-A[l]+10)%10!=num;

  if(r-l==1){
    if(B[l]-A[l]==B[r]-A[r]) return (B[l]-A[l]+10)%10!=num;
    return ((B[l]-A[l]+10)%10!=num)+((B[r]-A[r]+10)%10!=num);
  }

  if(used[l][r][num]++) return mem[l][r][num];

  int res=1e9;
  for(int i=l;i<r;i++)
    for(int j=0;j<10;j++)
      res=min(res,dfs(l,i,j)+dfs(i+1,r,j)+(num!=j));

  return mem[l][r][num]=res;
}


