  while(cin>>k,k){
  cin>>A>>B;
  memset(used,0,sizeof(used));
  int ans=dfs(0,k-1,0);
  for(int i=1;i<10;i++)ans=min(ans,dfs(0,k-1,i)+1);
  cout <<max(1,ans)<<endl;
  }  
  return 0;
}

