string rot_n(string s,int start,int len,int rot) {
    for(int i=0; i<len; ++i) {
        s[start+i] -= rot;
        if(s[start+i] < '0') s[start+i] += 10;
        if(s[start+i] > '9') s[start+i] -= 10;
    }
    return s;
}

int dfs(const string &s,int c) {
    //cout<<s<<" "<<c<<" "<<t<<endl;
    //if(memo.find(s) != memo.end()) return memo[s];
    if(s == goal) return 0;
    if(c == s.length()) return 1<<29;
    if(s[c] == goal[c]) return dfs(s,c+1);
    int res = 1<<29;
    for(int i=1; i<=(s.length()-c); ++i) {
        res = min(res, dfs(rot_n(s,c,i,s[c]-goal[c]),c+1) + 1);
    }
    return res;
}

int main() {
    int k;
    string s;
    while(cin>>k,  k) {
        cin>>s>>goal;
        memo.clear();
        memo[goal] = 0;
        cout<<dfs(s,0)<<endl;
    }
}

