#define INF INT_MAX/2

typedef vector<int> vi;
typedef vector<vi> vvi;
typedef string Node;
typedef pair<Node, int> pni;

struct Comp {
	bool operator() (pni a, pni b) {
		return a.second > b.second;
	}
};

string rot(string a, int from, int to, int n)
{
	rep(i, from, to + 1)
	{
		int j = a[i] - '0';
		j = (j + n + 10) % 10;
		a[i] = '0' + j;
	}

	return a;
}

int solve(int k)
{
	string start, end;
	cin >> start >> end;

	priority_queue<pni, vector<pni>, Comp> que;
	map<Node, int> dist;
	set<Node> done;

	que.push(pni(start, 0));
	while (!que.empty())
	{
		Node n = que.top().first;
		int c = que.top().second;
		que.pop();

		if (done.find(n) != done.end()) continue;
		done.insert(n);

		if (n == end) return c;

		int from = 0;
		while (n[from] == end[from]) from++;
		int delta = (end[from] - '0') - (n[from] - '0');
		rep(to, from, k)
		{
			Node nnode = rot(n, from, to, delta);
			int nc = c + 1;
			if (dist.find(nnode) == dist.end()) dist[nnode] = INF;
			if (nc < dist[nnode])
			{
				dist[nnode] = nc;
				que.push(pni(nnode, nc));
			}
		}
	}
}

int main()
{
	while (1)
	{
		int k; cin >> k;
		if (k == 0) return 0;
