bool ok(vector<int>& a) {
    return a.size() == 2 && a.front() == 0;
}

bool same(vector<int>& a) {
    return a.size() == 2;
}

vector<int> comp(vector<int>& a) {
    vector<int> v;
    REP(i, (int)a.size()-1) {
        if (v.empty() || v.back() != a[i]) {
            v.push_back(a[i]);
        }
    }
    v.push_back(a.back());
    return v;
}

ll _hash(vector<int>& a) {
    ll h = (int)a.size() - 1;
    REP(i, (int)a.size() - 1)  h = h * 10 + a[i];
    return h;
}

bool solve_one() {
    cin >> K;
    if (K == 0) return false;
    cin >> S >> T;
    vector<int> A;
    REP(i, K) A.push_back((S[i] - T[i] + 10) % 10);
    A.push_back(0);

    queue<vector<int>> que;
    que.push(comp(A));
    int ans = 1 << 29;
    set<ll> st;

    while (!que.empty()) {
        vector<int> v = que.front();
        que.pop();
        ll h = _hash(v);
        if (st.find(h) != st.end()) continue;
        st.insert(h);
        if (ans <= v.back()) {
            break;
        }
        if (ok(v)) {
            ans = v.back();
            break;
        } else if (same(v)) {
            ans = v.back() + 1;
        }
        //REP(i, (int)v.size()) cout <<  v[i] << " ";
        //cout << endl;
        REP(i, (int)v.size()-2) if (v[i] != v[i+1]) {
            {
                vector<int> x = v;
                int org = v[i];
                for (int j = i; j >= 0 && v[j] == org; --j) x[j] = v[i+1];
                x.back() += 1;
                x = comp(x);
                if (st.find(_hash(x)) != st.end()) continue;
                que.push(x);
            }
            {
                vector<int> x = v;
                int org = v[i+1];
                for (int j = i+1; j < K && v[j] == org; --j) x[j] = v[i];
                x.back() += 1;
                x = comp(x);
                if (st.find(_hash(x)) != st.end()) continue;
                que.push(x);
            }
        }
    }

    cout << ans << "\n";

    return true;
}

void solve() {
    while (solve_one()) ;
}

