int ans;

void calc(const int idx, vector<int> rests, int sum) {
	if(idx == k) {
		ans = sum;
		return;
	}
	if(rests[idx] == 0) {
		calc(idx + 1, rests, sum);
		return;
	}
	if(sum + 1 >= ans) {
		return;
	}

	for(int i = idx + 1; i < k; ++i) {
		rests[i] = (rests[i] - rests[idx] + mod) % mod;
		calc(idx + 1, rests, sum + 1);
	}
}

