        now.clear(); target.clear();
        now.resize(N); target.resize(N);
        string s, t;
        cin >> s >> t;
        for (int i = 0; i < N; i++) {
            now[i] = (s[i]-'0');
            target[i] = (t[i]-'0');
        }
        int ans = N;
        for (int i = 0; i < ans; i++) {
            if (dfs(0, i)) {
                ans = i;
                break;
            }
        }
        cout << ans << endl;
    }
    return 0;
}

