int func(int x, vector<int> n1){
	if(x == n) return 0;
	if(n1[x] == n2[x]){
		return func(x+1,n1);
	}
	int ret = INF;
	vector<int> tmp = n1;
	for(int i = 0; i < n-x; i++){
		if(n1[x] > n2[x]){
			for(int j = x; j < x+i+1; j++){
				tmp[j] -= n1[x]-n2[x];
				if(tmp[j] < 0) tmp[j]+=10;
			}
			ret = min(ret,func(x+1,tmp));
