#define rep(i,n) for(int i=0; i<n; ++i)
#define reep(i,f,n) for(int i=f; i<n; ++i)

int k;

void comp(char* s)
{
	rep(i, k-1){
		if(s[i] == s[i+1]){
			reep(j, i+1, k-1)
				s[j] = s[j+1];
			--k;
			--i;
		}
	}
}

