			s[i] -= s2[i];
			if(s[i] < 0)
				s[i] += 10;
		}
		comp(s);

		int dp[10][10] = {{0}};
		int zero[10][10] = {{0}};
		rep(i, k){
			for(int j=i; j>=0; --j){
				if(j<i){
					dp[j][i] = dp[j+1][i];
					zero[j][i] = zero[j+1][i];
				}
				zero[j][i] += !s[j];

				reep(l, j+1, i+1){
					if(s[j] == s[l]){
						int tmp = dp[j+1][l-1] + dp[l+1][i] + 1;
						int z = zero[l+1][i] + !s[j];
						if(dp[j][i] < tmp || (dp[j][i] == tmp && zero[j][i] < z)){
							dp[j][i] = tmp;
							zero[j][i] = z;
						}
					}
				}
			}
		}
		int ans = k - dp[0][k-1] - !!zero[0][k-1];
