int solve(string ini, string unlock)
{
  queue<string> que;
  d.clear();
  que.push(ini);
  d[ini] = 0;
  while(!que.empty()) {
    string s = que.front(); que.pop();
    if(s == unlock) break;
    string t = s; int idx = 0, rol = 0;
    while(t[idx] == unlock[idx]) idx++;
    while((t[idx]-'0'+rol) % 10 != unlock[idx]-'0') rol++;
