void dfs(vector<int> v2, int pos, int res){
  if(ans <= res) return;

  for(int i=pos;i<n;i++){
    if(v2[i] == 0) pos++;
    else break;
  }

  if(pos >= n){
    ans = min(ans,res);
    return;
  }

  int cnt = v2[pos];
  for(int i=pos;i<n;i++){
    v2[i] = fix(v2[i] - cnt);
    dfs(v2, pos+1, res+1);
  }
}

