		}
		comp(s);

		int dp[10][10] = {{0}};
		rep(i, k){
			for(int j=i; j>=0; --j){
				if(j<i) dp[j][i] = dp[j+1][i];
				reep(l, j+1, i+1){
					if(s[j] == s[l]){
						dp[j][i] = max(dp[j][i], dp[j+1][l-1] + dp[l+1][i] + 1);
