#define MAX_K 10

using namespace std;

struct dial{
	dial():depth(0){
		for(int i = 0; i < MAX_K; ++i)
			d[i] = -1;
	}
	char depth;
	char d[MAX_K];
	int diff(const dial &t)const{
		int ret = 0;
		for(int i = 0; i < MAX_K; ++i){
			if( d[i] != t.d[i] )
				++ret;
		}
		return ret;
	}
};

bool DialLock(int limit, int K, const dial &now, const dial &unlock){
	if( now.depth > limit )
		return false;

	if( 0 == unlock.diff( now ) ){
		return true;
	}

	for(int i = 0; i < K; ++i){
		int rot;
		if( now.d[i] != unlock.d[i] ){
			dial t = now;

			++t.depth;
			rot = unlock.d[i] - now.d[i];
			rot += (rot<0?MAX_K:0);

			for(int m = 0; m < K - i; ++m){
				t.d[i+m]=(now.d[i+m]+rot)%MAX_K;
				if( DialLock(limit,K,t,unlock) )
					return true;
			}

			break;
		}
	}
	return false;
}
