typedef long long ll;

ll A,B;
map<ll,int> cost,visited;
int n;

ll pow10[10];
int calc(ll x,int j){
  return x/pow10[j]%10;
}

int main(){
  pow10[0]=1;
  for(int i=1;i<10;i++)
    pow10[i]=pow10[i-1]*10LL;

  while(1){
    cin>>n;
    if(n==0)break;
    cost.clear();
    visited.clear();
    cin>>A>>B;    
    queue<ll> Q;
    visited[A]=1;
    cost[A]=0;
    Q.push(A);

    while(!Q.empty()){
      ll s=Q.front();Q.pop();

      int k;
      for(k=0;k<n;k++)
        if(calc(s,k)!=calc(B,k))break;
      if(k==n)break;
      ll next=s;
      int dist=(calc(B,k)-calc(s,k)+10)%10;

      for(int j=k;j<n;j++){
        int X=calc(next,j);
        next-=X*pow10[j];
        X=(X+dist)%10;
        next+=X*pow10[j];
        if(visited[next])continue;
        visited[next]=1;
        cost[next]=cost[s]+1;
        Q.push(next);
      }
    }
    cout<<cost[B]<<endl;
  }
  return 0;
}

