	cin >> start >> end;

	priority_queue<pni, vector<pni>, Comp> que;
	map<Node, int> dist;
	set<Node> done;

	que.push(pni(start, 0));
	while (!que.empty())
	{
		Node n = que.top().first;
		int c = que.top().second;
		que.pop();

		if (done.find(n) != done.end()) continue;
		done.insert(n);

