		}
		rep(i,n){
			if(ok[num[i]]) continue;
			cnt++;
			ok[num[i]] = true;
			if(n1[num[i]] < n2[num[i]]){
				int x = n2[num[i]]-n1[num[i]];
				rep(j,n){
					n1[j] = n1[j]+x;
					if(n1[j] >= 10) n1[j] -= 10;
					if(n1[j] == n2[j]){
						ok[j] = true;
					}
				}
			} else{
				int x = n1[num[i]]-n2[num[i]];
				rep(j,n){
					n1[j] = n1[j]-x+10;
					if(n1[j] >= 10) n1[j] -= 10;
					if(n1[j] == n2[j]){
						ok[j] = true;
					}
				}
			}
		}
		ans = min(ans,cnt);
	} while(next_permutation(num,num+n));
	cout << ans << endl;
}

int main(){
	while(true){
		cin >> n;
		if(n == 0) break;
		solve();
	}
}

