typedef pair<int, string> State; 

bool allZero(const string &s) {
  for(int i = 0; i < s.size(); ++i) {
    if(s[i] != 0) return false;
  }
  return true;
}

string change(string str, int s, int e, char a) {
  for(int i = s; i < e; ++i) str[i] = (10+str[i]-a)%10;
  return str;
}

int solve(string start) {
  State s(0, start);
  queue<State> que;
  set<string> vis;
  que.push(s);
  vis.insert(s.second);
  while(!que.empty()) {
    s = que.front(); que.pop();
    /*
    for(int i = 0; i < s.second.size(); ++i) cout << (int)s.second[i];
    cout << endl;
    */
    string &str = s.second;
    if(allZero(s.second)) return s.first;
    int l;
    for(l = 0; l < str.size() && str[l] == 0; ++l);
    for(int i = l; i < str.size(); ++i) {
      State t(s.first+1, change(str, l, i+1, str[l]));
      if(vis.find(t.second) != vis.end()) continue;
      vis.insert(t.second);
      que.push(t);
    }
  }
  return -1;
}

