template<class T> inline void chmin(T& a, const T& b) { if(b < a) a = b; }

const int mod = 10;
int k;

int calc(const int idx, vector<int> rests) {
	if(idx == k) return 0;
	if(rests[idx] == 0) return calc(idx + 1, rests);

	int res = calc(idx + 1, rests) + 1;
	for(int i = idx + 1; i < k; ++i) {
		rests[i] = (rests[i] - rests[idx] + mod) % mod;
		chmin(res, calc(idx + 1, rests) + 1);
	}

	return res;
}

